import classes from './PageTab.module.css'

const PageTab = (props) => {

    const componentClass = props.component;

    function getTabHeight(tab) {

        var top = 0;
        var left = 0;

        while(tab.tagName != "BODY") {
            top += tab.offsetTop;
            tab = tab.offsetParent;
        }

        return top;

    }

    function navigateTo() {

        // get the componemnt vertical height
        const tab = document.querySelector("." + componentClass);
        const tabHeight = getTabHeight(tab)-200; // check if theres a better way to alternate 
        console.log(tabHeight);

        window.scrollTo(0, tabHeight);
    }

    return (
        <button onClick={navigateTo} className={classes.tab}>{props.tabName}</button>
    )
}

export default PageTab
